import React, { useState, useEffect } from "react";
import { Upload, message, Image } from "antd";
import Modal from "antd/lib/modal/Modal";
import { PlusOutlined } from "@ant-design/icons";
import { result } from "lodash";

const UploadButton = () => (
  <div>
    <PlusOutlined />
    <div style={{ marginTop: 8 }}>Upload Profile</div>
  </div>
);

function getBase64(img, callback) {
  const reader = new FileReader();
  reader.addEventListener("load", () => callback(reader.result));
  reader.readAsDataURL(img);
}

function beforeUpload(file) {
  const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";
  if (!isJpgOrPng) {
    message.error("You can only upload JPG/PNG file!");
  }
  const isLt2M = file.size / 1024 / 1024 < 1;
  if (!isLt2M) {
    message.error("Image must smaller than 2MB!");
  }
  return isJpgOrPng && isLt2M;
}

/**
 *
 * @param {object} file image object to upload as string64
 * @param {function} state setter for a image state format
 */
const handlePreview = async (file, setImageState) => {
  if (!file.url && !file.preview) {
    file.preview = await getBase64(file.originFileObj);
    console.log(file.preview);
  }

  setImageState({
    previewImage: file.url || file.preview,
    previewVisible: true,
    previewTitle:
      file.name || file.url.substring(file.url.lastIndexOf("/") + 1),
    value: file.url || file.preview,
  });
};

export default function ImageUploader({ setImageValue }) {
  const imageINITIAL = {
    previewVisible: false,
    previewImage: "",
    previewTitle: "",
    value: null,
  };
  const [imageState, setimageState] = useState(imageINITIAL);
  const [fileList, setFileList] = useState([]);

  const handleCancel = () => setimageState(imageINITIAL);

  const handlePreview = (file) => {
    setimageState({
      previewImage: file.url || file.thumbUrl,
      previewVisible: true,
    });
  };

  const handleInnerImage = (v) => {
    getBase64(v.file.originFileObj, (value) => {
      console.log(value);
      setimageState({
        ...imageState,
        previewImage: value,
        previewVisible: true,
      });
    });
    setFileList(v.fileList);
  };

  useEffect(() => {
    if (setImageValue) {
      setImageValue(imageState.value);
    }
  }, [imageState]);
  return (
    <>
      <Upload
        name="avatar"
        listType="picture-card"
        className="avatar-uploader"
        showUploadList={false}
        customRequest={() => {}}
        onPreview={handlePreview}
        onChange={handleInnerImage}
      >
        <Image
          alt="example"
          style={{ width: "100%" }}
          src={imageState.previewImage}
          fallback="data:image/png;base64,"
        />
      </Upload>
    </>
  );
}
